import React, { useState, useEffect } from 'react';
import axios from 'axios';

const App = () => {
    const [description, setDescription] = useState('');
    const [tasks, setTasks] = useState([]);

    useEffect(() => {
        fetchTasks();
    }, []);

    const fetchTasks = async () => {
        const response = await axios.get('http://localhost:5001/getTasks');
        setTasks(response.data);
    };

    const addTask = async () => {
        if (description !== '') {
            await axios.post('http://localhost:5001/addTask', { description });
            setDescription('');
            fetchTasks();
        }
    };

    const updateTask = async (id, completed) => {
        await axios.put(`http://localhost:5001/updateTask/${id}`, { completed });
        fetchTasks();
    };

    const deleteTask = async id => {
        await axios.delete(`http://localhost:5001/deleteTask/${id}`);
        fetchTasks();
    };

    return (
        <div>
            <h1>To-Do List</h1>
            <input
                type="text"
                value={description}
                onChange={e => setDescription(e.target.value)}
            />
            <button onClick={addTask}>Add Task</button>
            <h2>Tasks</h2>
            <ul>
                {tasks.map(task => (
                    <li key={task.id}>
                        <span
                            style={{ textDecoration: task.completed ? 'line-through' : 'none' }}
                            onClick={() => updateTask(task.id, !task.completed)}
                        >
                            {task.description}
                        </span>
                        <button onClick={() => deleteTask(task.id)}>Delete</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default App;
